package com.study.phoebus.controller;import com.study.phoebus.annotation.MyLog;import com.study.phoebus.entity.Student;import com.study.phoebus.response.BaseResponse;import com.study.phoebus.service.StudentService;import org.apache.shiro.SecurityUtils;import org.apache.shiro.subject.Subject;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping(value = "test")public class TestController {    @Autowired    private StudentService studentService;    @PostMapping(value = "add")    @MyLog(value = "添加")    public BaseResponse add(@RequestBody Student student){        Subject subject = SecurityUtils.getSubject();        System.out.println(subject);        studentService.insert(student);        BaseResponse response = new BaseResponse();        response.setCode("200");        response.setMsg("成功");        response.setData(student);        return response;    }    @GetMapping(value = "query")    @MyLog("查询")    public List<Student> query(String name,int age){        return studentService.query(name,age);    }}