package com.study.phoebus.entity.shiro;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import java.util.List;/** * @author myt_ac@163.com * @title: Role * @description: TODO * @date 2019/6/415:57 */@Documentpublic class Role {    @Id    private String id;    private String roleName;    private List<Permission> permissions;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getRoleName() {        return roleName;    }    public void setRoleName(String roleName) {        this.roleName = roleName;    }    public List<Permission> getPermissions() {        return permissions;    }    public void setPermissions(List<Permission> permissions) {        this.permissions = permissions;    }}