package com.study.phoebus.entity.shiro;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import java.io.Serializable;import java.util.List;/** * @author myt_ac@163.com * @title: User * @description: TODO * @date 2019/6/415:55 */@Documentpublic class User implements Serializable {    @Id    private String id;    private String name;    private String account;    private String password;    private List<Role> roles;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAccount() {        return account;    }    public void setAccount(String account) {        this.account = account;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public List<Role> getRoles() {        return roles;    }    public void setRoles(List<Role> roles) {        this.roles = roles;    }}