package com.study.syslog.controller;import com.study.syslog.annotation.MyLog;import com.study.syslog.entity.Student;import com.study.syslog.response.BaseResponse;import com.study.syslog.service.StudentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping(value = "test")public class TestController {    @Autowired    private StudentService studentService;    @PostMapping(value = "add")    @MyLog(value = "添加")    public BaseResponse add(@RequestBody Student student){         studentService.insert(student);        BaseResponse response = new BaseResponse();        response.setCode("200");        response.setMsg("成功");        response.setData(student);        return response;    }    @GetMapping(value = "query")    @MyLog("查询")    public List<Student> query(String name,int age){        return studentService.query(name,age);    }}