package com.study.syslog.service.impl;import com.study.syslog.dao.mongo.RoleRepository;import com.study.syslog.dao.mongo.UserRepository;import com.study.syslog.entity.shiro.Role;import com.study.syslog.entity.shiro.User;import com.study.syslog.service.ILoginService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @author myt_ac@163.com * @title: LoginServiceImpl * @description: TODO * @date 2019/6/416:25 */@Servicepublic class LoginServiceImpl implements ILoginService {    @Autowired    private UserRepository userRepository;    @Autowired    private RoleRepository roleRepository;    @Override    public User addUser(User user) {        return userRepository.save(user);    }    @Override    public Role addRole(Role role) {        return roleRepository.save(role);    }    @Override    public User findByAccount(String account) {        return userRepository.findByAccount(account);    }}