package com.study.syslog.service.impl;import com.study.syslog.dao.mongo.StudentRepository;import com.study.syslog.entity.Student;import com.study.syslog.service.StudentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class StudentServiceImpl implements StudentService {    @Autowired    private StudentRepository studentRepository;    @Override    public void insert(Student student) {        studentRepository.insert(student);    }    @Override    public List<Student> query(String name, int age) {        return studentRepository.queryByNameAndAge(name,age);    }}