package com.study.syslog.service.impl;import com.study.syslog.dao.mongo.SysLogRepository;import com.study.syslog.entity.SysLog;import com.study.syslog.service.SysLogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class SysLogServiceImpl implements SysLogService {    @Autowired    private SysLogRepository sysLogRepository;    @Override    public void saveSysLog(SysLog sysLog) {        sysLogRepository.insert(sysLog);    }}